SELECT TITLE,USED_GOODS_BOARD.BOARD_ID,REPLY_ID,USED_GOODS_REPLY.WRITER_ID,USED_GOODS_REPLY.CONTENTS,date_format(USED_GOODS_REPLY.CREATED_DATE,'%Y-%m-%d') as CREATED_DATE from USED_GOODS_BOARD inner join USED_GOODS_REPLY on USED_GOODS_BOARD.board_id = USED_GOODS_REPLY.board_id where date_format(USED_GOODS_BOARD.created_date,'%Y-%m') = '2022-10' order by CREATED_DATE,title;